package com.syntax.class02;

public class ReviewCodingJ {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 int box1=12;
  //char box1='S' - not allowed as creates confusion;
 
  int box2; // creating a box but not storing data right away in the box;
  box2=20;
 
  box1=30; // i am replacing the value 12 stored in box1 with 30;
 
	System.out.println(box1);
	System.out.println(box2);
	
	/*float
	 * doubles - most popular;
	 * 
	 * 
	 */
	float box3=12.5F;
	double box4=13.67;
	System.out.println(box3);
	System.out.println(box4);
	
	box1=10;
	box1=box1+5;
	System.out.println(box1);
	
	// byte-short-int-long-float-double;
	
	}

}
