package com.syntax.class03;

public class RecapOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	    //Addition => +
		// Subtraction=> -;
		//Multiplication=> *;
		//Division=> /;
		//Modulus => %;
		
		int number1=9;
		int number2=2;
		System.out.println("Answer= "+ number1/number2); //diving number1 by number 2
		System.out.println("Remainder= "+ number1%number2); 
		
		double number3=1;
		double number4=3;
		System.out.println("Answer= "+number3/number4); 
		
		float number5=1.0F;
		float number6=3.0F;
		System.out.println("Answer= "+number5/number6); 
		
		double number7=1;
		double number8=3;
		System.out.println("Answer= "+number7/number8); 
		
	}

}
