package com.syntax.class07;

public class HomeWork1and2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=1;
		while(a<=100) {
			System.out.print(a+" ");
		    a++;	
		}
		System.out.println("------------------------------");
		
		int b=100;
		while(b>=1) {
			System.out.println(b);
		    b--;	
		}
		System.out.println("------------------------------");
		
		
	}

}
